require_relative 'bathroom_code'

describe BathroomCode do

   it 'can handles the up instruction in each direction' do
     expect(BathroomCode.new.from_instructions(['U'])).to eq('2')
     expect(BathroomCode.new.from_instructions(['D'])).to eq('8')
     expect(BathroomCode.new.from_instructions(['L'])).to eq('4')
     expect(BathroomCode.new.from_instructions(['R'])).to eq('6')
   end

   it 'handles consecutive move instructions' do
     expect(BathroomCode.new.from_instructions(['UL'])).to eq('1')
   end

   it 'limits the moves to each bound' do
     expect(BathroomCode.new.from_instructions(['LL'])).to eq('4')
     expect(BathroomCode.new.from_instructions(['UU'])).to eq('2')
     expect(BathroomCode.new.from_instructions(['DD'])).to eq('8')
     expect(BathroomCode.new.from_instructions(['RR'])).to eq('6')
   end

  it 'can handle a series of key instructions' do
    series_of_instructions = %w(ULL RRDDD LURDL UUUUD)

    expect(BathroomCode.new.from_instructions(series_of_instructions)).to eq('1985')
  end

  it 'solves the riddle' do
    series_of_instructions = %w(
      
      
      
      
      )
    expect(BathroomCode.new.from_instructions(series_of_instructions)).to eq('78293')
  end

end